def _parse_puzzle1(indata: str) -> tuple[Map, Point]:
    infile = io.StringIO(indata)

    start = Point(row=0, col=0)
    topomap = []

    for row, line in enumerate(filter(bool, map(str.strip, infile))):
        cols = []

        for col, cell in enumerate(line):
            point = Point(row=row, col=col, cell=cell)
            if cell == '@':
                start = point

            cols.append(point)
        topomap.append(cols)

    return Map(data=topomap), start


def _parse_moves1(indata: str) -> list[str]:
    infile = io.StringIO(indata)

    moves = []

    for line in filter(bool, map(str.strip, infile)):
        moves.extend(list(line))

    return moves


def parse1(indata: str) -> tuple[Map, Point, list[str]]:  # noqa:WPS210
    puzzle, moves_str = indata.split('\n\n')

    topomap, start = _parse_puzzle1(puzzle)
    moves = _parse_moves1(moves_str)

    return topomap, start, moves


def _parse_puzzle2(indata: str) -> list[list[str]]:
    infile = io.StringIO(indata)

    topomap = []

    for line in filter(bool, map(str.strip, infile)):
        topomap.append(list(line))

    return topomap


def _parse_moves2(indata: str) -> list[str]:
    infile = io.StringIO(indata)

    moves = []

    for line in filter(bool, map(str.strip, infile)):
        moves.extend(list(line))

    return moves


def parse2(indata: str) -> tuple[list[list[str]], list[str]]:  # noqa:WPS210
    puzzle, moves_str = indata.split('\n\n')

    return _parse_puzzle2(puzzle), _parse_moves2(moves_str)


class TestDay15:
    @pytest.mark.parametrize(
        'indata, want',
        [(_example1, 2028), (_example2, 10092), (_input, 1568399)],
        ids=('example 1', 'example 2', 'result'),
    )
    def test_first_star(self, indata: str, want: int) -> None:
        topomap, start, moves = parse1(indata)

        got = first_star(topomap, start, moves)

        assert got == want, f'first_star() = {got}, want = {want}'

    @pytest.mark.parametrize(
        'indata, want',
        [(_example3, 618), (_example2, 9021), (_input, 1575877)],
        ids=('example 1', 'example 2', 'result'),
    )
    def test_second_star(self, indata: str, want: int) -> None:
        topomap, moves = parse2(indata)

        got = second_star(topomap, moves)

        assert got == want, f'second_star() = {got}, want = {want}'
